pragma solidity 0.8.9;

import "./IScaledBalanceToken.sol";

interface IVariableDebtToken is IScaledBalanceToken {
  /**
   * @dev Emitted after the mint action
   * @param user The address performing the mint
   * @param value The amount to be minted
   * @param index The last index of the reserve
   **/
  event Mint(address indexed user, uint256 value, uint256 index);

  /**
   * @dev Mints debt token to the `onBehalfOf` address
   * @param user The address receiving the borrowed underlying and the debt token
   * @param amount The amount of debt being minted
   * @param index The variable debt index of the reserve
   **/
  function mint(
    address user,
    uint256 amount,
    uint256 index
  ) external;

  /**
   * @dev Emitted when variable debt is burnt
   * @param user The user which debt has been burned
   * @param amount The amount of debt being burned
   * @param index The index of the user
   **/
  event Burn(address indexed user, uint256 amount, uint256 index);

  /**
   * @dev Burns user variable debt
   * @param user The user which debt is burnt
   * @param index The variable debt index of the reserve
   **/
  function burn(
    address user,
    uint256 amount,
    uint256 index
  ) external;
}","keccak256":"0x264b53c527d943ba2bf623bc81100ba006399d6226840fc5eaf262abd9e98647","license":"MIT"},"contracts/tokenization/VariableDebtToken.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "./base/DebtTokenBase.sol";
import "../helper/WadRayMath.sol";
import "../helper/MathUtils.sol";
import "../interfaces/ILendingPool.sol";
import "../interfaces/IVariableDebtToken.sol";

/**
 * @title VariableDebtToken
 * @notice Implements a variable debt token to track the borrowing positions of users
 * at variable rate mode
 **/
contract VariableDebtToken is Initializable, DebtTokenBase, IVariableDebtToken {
    using SafeMath for uint256;
    using WadRayMath for uint256; 

    ILendingPool internal _pool;

  function initialize(
    ILendingPool pool,
    string calldata tokenName,
    string calldata tokenSymbol
  ) external initializer {
    _pool = pool;
    _setName(tokenName);
    _setSymbol(tokenSymbol);
  }

   /**
   * @dev Calculates the accumulated debt balance of the user
   * @return The debt balance of the user
   **/
  function balanceOf(address user) public view virtual override returns (uint256) {
    uint256 scaledBalance = super.balanceOf(user);

    if (scaledBalance == 0) {
      return 0;
    }

    return scaledBalance.rayMul(_pool.getReserveNormalizedVariableDebt());
  }

  /**
   * @dev Mints debt token to the `onBehalfOf` address
   * -  Only callable by the LendingPool
   * @param user The address receiving the borrowed underlying and the debt tokens
   * @param amount The amount of debt being minted
   * @param index The variable debt index of the reserve
   **/
  function mint(
    address user,
    uint256 amount,
    uint256 index
  ) external override onlyLendingPool {

    uint256 amountScaled = amount.rayDiv(index);

    _mint(user, amountScaled);

    emit Transfer(address(0), user, amount);
    emit Mint(user, amount, index);
  }

  /**
   * @dev Burns user variable debt
   * - Only callable by the LendingPool
   * @param user The user whose debt is getting burned
   * @param amount The amount getting burned
   * @param index The variable debt index of the reserve
   **/
  function burn(
    address user,
    uint256 amount,
    uint256 index
  ) external override onlyLendingPool {
    uint256 amountScaled = amount.rayDiv(index);
    require(amountScaled != 0, "INVALID_BURN_AMOUNT");

